import React from "react";
import MyContent from "./MyContent.js";


class MyMenu extends React.Component {
  constructor(props) {
    super(props);
    this.state = {displayedPage : ""
    };
  }

  render() {
    let { } = this.props;
    return ( 
        <div>
        
    	<ul>
    		<li onClick={() => alert("Surprise")}>
    			Login Form
    		</li>
    		<li>
    			New Form
    		</li>
    		<li>
    			Search
    		</li>
    		<li onClick={() => this.setState({...this.state, displayedPage:"orgList"})}>
    			Org List
    		</li>
    	</ul>
        <MyContent displayedPage={this.state.displayedPage}/>
        </div>
      
    );
  }
}

export default MyMenu;
